import React from 'react';


// material-ui
import { CardContent, Checkbox, FormControlLabel, Grid, MenuItem, TextField } from '@material-ui/core';

// project imports
import { gridSpacing } from 'store/constant';
import SubCard from 'ui-component/cards/SubCard';
import MaskedInput from 'react-text-mask';

import { withFormik } from 'formik';
import * as Yup from 'yup';

import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';

import * as dashboardActions from 'store/actions/dashboardActions';
// const validationSchema = Yup.object({
//     referral_client_email: Yup
//         .string('Enter your email')
//         .email('Enter a valid email')
//         .required('Email is required')
// });

const formikEnhancer = withFormik({
    validationSchema: Yup.object().shape({
        userId: Yup.string().email()
            .required('Email is required.'),
    }),
    mapPropsToValues: () => ({
        userId: 'test',
        userPassword: 'test'
    }),
    handleSubmit: (payload, { props, setSubmitting }) => {
        // props.loginUser(payload);
        setSubmitting(false);
    },
    displayName: 'LoginForm'
})(LoginForm);



const ClientDetail = ({ clientFormik }) => <Grid container spacing={gridSpacing}>
        <Grid item xs={12} sm={12} lg={12} md={12}>
            <SubCard title='Client Details'>
                <CardContent>
                    <Grid container spacing={gridSpacing}>
                        <Grid item xs={8} sm={10} lg={10} md={10}>
                            <Grid container spacing={gridSpacing}>
                                <Grid item xs={6} sm={8} lg={6} md={8}>
                                    <TextField name='referral_client_first_name' id='referral_client_first_name'
                                               fullWidth label='First Name'
                                               value={clientFormik.values.referral_client_first_name}
                                               onChange={clientFormik.handleChange} />
                                </Grid>
                                <Grid item xs={6} sm={8} lg={6} md={8}>
                                    <TextField name='referral_client_last_name' fullWidth label='Last Name'
                                               value={clientFormik.values.referral_client_last_name}
                                               onChange={clientFormik.handleChange} />
                                </Grid>
                            </Grid>
                        </Grid>

                        <Grid item xs={8} sm={8} lg={8} md={8}>
                            <Grid container spacing={gridSpacing}>
                                <Grid item xs={12} sm={12} lg={8} md={8}>
                                    <TextField name='referral_client_email' fullWidth label='Email'
                                               value={clientFormik.values.referral_client_email}
                                               onChange={clientFormik.handleChange}
                                               error={clientFormik.touched.email && Boolean(clientFormik.errors.email)} />
                                </Grid>
                            </Grid>
                        </Grid>


                        <Grid item xs={8} sm={8} lg={8} md={8}>
                            <Grid container spacing={gridSpacing}>
                                <Grid item xs={12} sm={12} lg={8} md={8}>

                                    <button type='submit'>
                                        Submit
                                    </button>

                                </Grid>
                            </Grid>
                        </Grid>

                    </Grid>
                </CardContent>
            </SubCard>
        </Grid>
    </Grid>
;

function mapStateToProps(state) {
    return {...state};
}

function mapDispatchToProps(dispatch) {
    return bindActionCreators({
        ...dashboardActions,
    }, dispatch);
}

export default connect(mapStateToProps, mapDispatchToProps)(ClientDetail);
