import React from 'react';

// material-ui
import {
    CardContent,
    FormControl,
    FormControlLabel,
    Grid,
    Card,
    Radio,
    RadioGroup,
    TextField,
    Button
} from '@material-ui/core';

// project imports
import {gridSpacing} from 'store/constant';
import SubCard from 'ui-component/cards/SubCard';
import MaskedInput from 'react-text-mask';


import {useFormik, withFormik} from 'formik';
import * as Yup from 'yup';


import {makeStyles} from '@material-ui/styles';
import * as referralActions from 'store/actions/reviewBoard/referralActions'
import {connect} from 'react-redux';


const useStyles = makeStyles((theme) => ({
    card: {
        backgroundColor: theme.palette.mode === 'dark' ? theme.palette.dark[800] : theme.palette.primary.light,
        marginBottom: '16px',
        marginTop: '16px'
    }
}));

const CaseManagementDecision = ({}) => {
    const classes = useStyles();
    const [valueLabel, setValueLabel] = React.useState('checked');

    return (
        <Card className={classes.card} title='Client Case Management Decision'>
            <CardContent>
                <form onSubmit={formik.handleSubmit} noValidate>
                    <Grid container spacing={3} direction='column'>
                        <Grid item>
                            <FormControl>
                                <RadioGroup
                                    row
                                    aria-label='gender'
                                    value={valueLabel}
                                    onChange={(e) => setValueLabel(e.target.value)}
                                    name='row-radio-buttons-group'
                                >
                                    <FormControlLabel valudashboardActionse='checked' control={<Radio/>}
                                                      label='Client is accepted in the Case Management Program'/>
                                    <FormControlLabel value='unchecked' control={<Radio/>}
                                                      label='Client is refused for the Case Management Program'/>
                                </RadioGroup>
                            </FormControl>
                        </Grid>
                        <Grid item xs={12}>
                            <TextField
                                fullWidth
                                maxRows={10}
                                id='client_referral_decision_reason'
                                name='client_referral_decision_reason'
                                label='Reason for Client Refusal'
                                placeholder='Enter reason here'
                                multiline
                                value={formik.values.client_referral_decision_reason}
                                onChange={formik.handleChange}
                            />
                        </Grid>

                        <Grid item xs={6} md={6} lg={6}>
                            <Button color='primary' variant='contained' fullWidth type='submit'>
                                Submit
                            </Button>
                        </Grid>
                    </Grid>
                </form>
            </CardContent>
        </Card>
    );
};

function mapStateToProps(state) {
    return {...state};
}

function mapDispatchToProps(dispatch) {
    return {
        // setClientReferralDecisionReason: (clientReferralDecisionReason) => dispatch(referralActions.setClientReferralDecisionReason(clientReferralDecisionReason))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(CaseManagementDecision);
