import React from 'react';

import { Formik, Form, Field, ErrorMessage, useFormik } from 'formik';
import * as Yup from 'yup';

// material-ui
import { CardContent, Checkbox, FormControlLabel, Grid, MenuItem, TextField } from '@material-ui/core';

// project imports
import { gridSpacing } from 'store/constant';
import SubCard from 'ui-component/cards/SubCard';
import MaskedInput from 'react-text-mask';

const validationSchema = Yup.object({
    email: Yup
        .string('Enter your email')
        .email('Enter a valid email')
        .required('Email is required')
});

const formik = useFormik({
    initialValues: {
        email: 'saichitta@ccc.com'
    },
    validationSchema,
    onChange: (values) => {
        alert(JSON.stringify(values, null, 2));
    }
});

const ClientDetail = () => (
    <Formik
        initialValues={{
            firstName: '',
            lastName: '',
            email: ''
        }}
        onChange={async (values) => {
            console.log('OK');
            console.log(values);
            // await new Promise((r) => setTimeout(r, 500));
            // alert(JSON.stringify(values, null, 2));
        }}
    >
        <Form>
            <Grid container spacing={gridSpacing}>
                <Grid item xs={12} sm={12} lg={12} md={12}>
                    <SubCard title='Client Details'>
                        <CardContent>
                            <Grid container spacing={gridSpacing}>
                                <Grid item xs={8} sm={10} lg={10} md={10}>
                                    <Grid container spacing={gridSpacing}>
                                        <Grid item xs={6} sm={8} lg={6} md={8}>
                                            <TextField name='referral_client_first_name' fullWidth label='First Name'
                                                       defaultValue='' />
                                        </Grid>
                                        <Grid item xs={6} sm={8} lg={6} md={8}>
                                            <TextField name='referral_client_last_name' fullWidth label='Last Name'
                                                                                    defaultValue='' />
                                        </Grid>
                                    </Grid>
                                </Grid>

                                <Grid item xs={8} sm={8} lg={8} md={8}>
                                    <Grid container spacing={gridSpacing}>
                                        <Grid item xs={12} sm={12} lg={8} md={8}>
                                            <TextField name='referral_client_email' fullWidth label='Email' defaultValue='' />
                                        </Grid>
                                    </Grid>
                                </Grid>
                            </Grid>
                        </CardContent>
                    </SubCard>
                </Grid>
            </Grid>
        </Form>
    </Formik>
);

export default ClientDetail;
