import React from 'react';

// material-ui
import { CardContent, Checkbox, FormControlLabel, Grid, MenuItem, TextField } from '@material-ui/core';

// project imports
import { gridSpacing } from 'store/constant';
import SubCard from 'ui-component/cards/SubCard';
import AssessmentForms from '../../../common/assessment-forms';
import MaskedInput from 'react-text-mask';

import { makeStyles } from '@material-ui/styles';

const modeOfInterventionSelectList = [
    {
        value: 'in-person-home',
        label: 'In-Person Visit - Home'
    },
    {
        value: 'in-person-nursing-home',
        label: 'In-Person Visit - Nursing Home '
    },
    {
        value: 'in-person-residential-facility',
        label: 'In-Person Visit - Residential Facility '
    },
    {
        value: 'in-person-work',
        label: 'In-Person Visit - Work'
    },
    {
        value: 'in-person-other',
        label: 'In-Person Visit - Other'
    },
    {
        value: 'telephone',
        label: 'Telephone'
    },
    {
        value: 'videoconferencing',
        label: 'Videoconferencing'
    },
    {
        value: 'other',
        label: 'Other'
    }
];

const useStyles = makeStyles((theme) => ({
    card: {
        backgroundColor: theme.palette.mode === 'dark' ? theme.palette.dark[800] : theme.palette.primary.light,
        marginBottom: '16px',
        marginTop: '16px'
    }
}));

const TypeOfClinicalInterventions = () => {
    const classes = useStyles();

    const [sdm, setSdm] = React.useState(true);
    const [notification, setNotification] = React.useState(false);

    return (
        <Card className={classes.card}>
            <CardContent>
                <Grid container spacing={3} direction="column">
                    <Grid item>
                        <Grid item container alignItems="center" justifyContent="space-between">
                            <Grid item>
                                <Typography variant="subtitle1">Start DND Mode</Typography>
                            </Grid>
                            <Grid item>
                                <Switch size="small" color="primary" checked={sdm} onChange={(e) => setSdm(e.target.checked)} name="sdm" />
                            </Grid>
                        </Grid>
                    </Grid>
                    <Grid item>
                        <Grid item container alignItems="center" justifyContent="space-between">
                            <Grid item>
                                <Typography variant="subtitle1">Allow Notifications</Typography>
                            </Grid>
                            <Grid item>
                                <Switch
                                    size="small"
                                    checked={notification}
                                    onChange={(e) => setNotification(e.target.checked)}
                                    name="sdm"
                                />
                            </Grid>
                        </Grid>
                    </Grid>
                </Grid>
            </CardContent>
        </Card>
    );
};

export default TypeOfClinicalInterventions;
