// action - state management
import * as actionTypes from 'store/actionTypes';

export const initialState = {
    clientDetail: {
        firstName: '',
        lastName: '',
        email: ''
    },
    referralDetail: {
      referralDate: '',
        
    },
    referralForms: {},
    casePresentationForm: {},
    decision: {}
};

const sectionFormReducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.REVIEW_BOARD_CLIENT_REFERRAL_SET_DECISION: {
            return {
                ...state
            };
        }
        case actionTypes.REVIEW_BOARD_CLIENT_REFERRAL_SET_DETAIL: {
            // eslint-disable-next-line camelcase
            const {client_referral_first_name, client_referral_last_name, client_referral_email} = action.data
            return {
                ...state,
                clientDetail: {
                    firstName: client_referral_first_name,
                    lastName: client_referral_last_name,
                    email: client_referral_email
                }
            };
        }
        default: {
            return {...state};
        }
    }
};

export default sectionFormReducer;
