import * as actionTypes from 'store/actionTypes';
import apiClient from 'store/api-client';

export const setClientReferralDetail = (values) => ({
    type: actionTypes.REVIEW_BOARD_CLIENT_REFERRAL_SET_DETAIL,
    data: {
        ...values
    }
})

export const setClientReferralDecisionReason = (decisionValue) => ({
    type: actionTypes.REVIEW_BOARD_CLIENT_REFERRAL_SET_DECISION,
    data: {
        decision: decisionValue
    }
})
