import React from 'react';

// material-ui
import {
    CardContent,
    FormControl,
    FormControlLabel,
    Grid,
    Card,
    Radio,
    RadioGroup,
    TextField,
    Button
} from '@material-ui/core';

// project imports
import { gridSpacing } from 'store/constant';
import SubCard from 'ui-component/cards/SubCard';
import MaskedInput from 'react-text-mask';


import { useFormik, withFormik } from 'formik';
import * as Yup from 'yup';


import { makeStyles } from '@material-ui/styles';


const useStyles = makeStyles((theme) => ({
    card: {
        backgroundColor: theme.palette.mode === 'dark' ? theme.palette.dark[800] : theme.palette.primary.light,
        marginBottom: '16px',
        marginTop: '16px'
    }
}));


const CaseManagementDecision = () => {
    const classes = useStyles();
    const [valueLabel, setValueLabel] = React.useState('checked');

    const validationSchema = Yup.object({
        referral_client_email: Yup
            .string('Enter your decision')
            .required('Decision is required')
    });

    const formik = useFormik({
        initialValues: {
            client_referral_decision_reason: 'foobar@example.com',
        },
        validationSchema,
        onSubmit: (values) => {
            console.log('OK1');
            alert(JSON.stringify(values, null, 2));
        }
    });

    return (
        <Card className={classes.card} title='Client Case Management Decision'>
            <CardContent>
                <form onSubmit={formik.handleSubmit}>
                    <Grid container spacing={3} direction='column'>
                        <Grid item>
                            <FormControl>
                                <RadioGroup
                                    row
                                    aria-label='gender'
                                    value={valueLabel}
                                    onChange={(e) => setValueLabel(e.target.value)}
                                    name='row-radio-buttons-group'
                                >
                                    <FormControlLabel value='checked' control={<Radio />}
                                                      label='Client is accepted in the Case Management Program' />
                                    <FormControlLabel value='unchecked' control={<Radio />}
                                                      label='Client is refused for the Case Management Program' />
                                </RadioGroup>
                            </FormControl>
                        </Grid>
                        <Grid item xs={12}>
                            <TextField
                                fullWidth
                                maxRows={10}
                                id='client_referral_decision_reason'
                                label='Reason for Client Refusal'
                                placeholder='Enter reason here'
                                multiline
                                name='client_referral_decision_reason'
                                value={formik.values.client_referral_decision_reason}
                                onChange={formik.handleChange}
                            />


                            <Button color='primary' variant='contained' fullWidth type='submit'>
                                Submit
                            </Button>
                        </Grid>
                    </Grid>
                </form>
            </CardContent>
        </Card>
    );
};

export default CaseManagementDecision;
